<?php//	Function to add the tabs to the pagefunction add_tabs( $dotab ) {    $tabs = array( 'report' => __( 'Stock report', 'stockmanager' ), 'settings' => __( 'Settings', 'woocommerce' ), 'history' => __( 'History', 'stockmanager' ) );    $links = array();	if ( $dotab == null ) {		$dotab = 'report';	}    foreach( $tabs as $tab => $name ) {        if ( $tab == $dotab ) {            $links[] = "<a class='nav-tab nav-tab-active' href='?page=stock-manager&tab=$tab'>$name</a>";        } else {            $links[] = "<a class='nav-tab' href='?page=stock-manager&tab=$tab'>$name</a>";        }    }    echo '<h2>';    foreach ( $links as $link ) {        echo $link;	}    echo '</h2>';}// testing the users admin rightsif ( !current_user_can('administrator')) {    die('No Access to this functionality!');}//	Building the actual pageif ( isset ( $_GET[ 'tab' ] ) ) {    $tab = $_GET[ 'tab' ];} else {    $tab = 'report';}add_tabs( $tab );global $app_version;echo '<div class="wrap">';echo screen_icon( 'options-general' ); echo '<h2>Stock Manager ' . __( 'version', 'stockmanager' ) . ' ' . $app_version . '</h2>';echo '</div>';if( isset( $_GET[ 'settings-updated' ] ) ) {    echo '<div id="message" class="updated">';		echo '<p><strong>' . _e('Settings saved.') . '</strong></p>';    echo '</div>';}switch ( $tab ) {    case 'report' :		include( 'stockmanager-report.php' );		if ( isset( $_POST[ 'go' ] ) ) {			process_stock_report();		}		build_stock_report();        break;    case 'settings' :		include( 'stockmanager-options.php' );		plugin_options_page();        break;    case 'history' :		include( 'stockmanager-history.php' );		if ( isset ( $_GET[ 'id' ] ) ) {			stockreport_history_details_page();		} else {			stockreport_history_page();		}        break;}?>